mapper.select:
select 
count(*)
from forestry_wetlands
where 1=1

   and created_at = #{createdAt,jdbcType=DATETIME}


  and  created_by = #{createdBy,jdbcType=VARCHAR}


   and updated_at = #{updatedAt,jdbcType=TIMESTAMP}


  and updated_by = #{updatedBy,jdbcType=VARCHAR}


   and period_start > #{periodStart,jdbcType=TIMESTAMP}


   and period_end  <=  #{periodEnd,jdbcType=TIMESTAMP}


   and name like CONCAT('%', #{name,jdbcType=VARCHAR},'%')


   and area like CONCAT('%', #{area,jdbcType=VARCHAR},'%')

select 

from forestry_wetlands
where 1=1

   and created_at = #{createdAt,jdbcType=DATETIME}


  and created_by = #{createdBy,jdbcType=VARCHAR}


   and updated_at = #{updatedAt,jdbcType=TIMESTAMP}


   and updated_by = #{updatedBy,jdbcType=VARCHAR}


   and name  like CONCAT('%', #{name,jdbcType=VARCHAR},'%')


   and area like CONCAT('%', #{area,jdbcType=VARCHAR},'%')


   and period_start > #{periodStart,jdbcType=TIMESTAMP}


   and period_start <= #{periodEnd,jdbcType=TIMESTAMP}

  limit #{pageIndex,jdbcType=INTEGER} ,#{pageSize,jdbcType=INTEGER}
